package com.itranswarp.wxapi.sample.controller;

import java.util.Base64;

import javax.servlet.http.HttpServletRequest;

import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.web.bind.annotation.RequestMethod;
import org.springframework.web.bind.annotation.RestController;

import com.itranswarp.wxapi.material.ImageList;
import com.itranswarp.wxapi.material.MaterialCount;
import com.itranswarp.wxapi.material.VideoList;
import com.itranswarp.wxapi.material.VoiceList;

@RestController
public class MaterialController extends AbstractController {

	@RequestMapping(value = "/material/images", method = RequestMethod.GET)
	public ImageList getAllImages() {
		return client.materialImageList(0, 20);
	}

	@RequestMapping(value = "/material/videos", method = RequestMethod.GET)
	public VideoList getAllVideos() {
		return client.materialVideoList(0, 20);
	}

	@RequestMapping(value = "/material/voices", method = RequestMethod.GET)
	public VoiceList getAllVoices() {
		return client.materialVoiceList(0, 20);
	}

	@RequestMapping(value = "/material/count", method = RequestMethod.GET)
	public MaterialCount count() {
		return client.materialCount();
	}

	@RequestMapping(value = "/upload/image", method = RequestMethod.GET)
	public Object token(HttpServletRequest request) throws Exception {
		log.debug("will upload image...");
		byte[] imageData = Base64.getDecoder().decode(IMAGE_DATA);
		return client.uploadImage("test.jpg", imageData);

	}

	static final String IMAGE_DATA = "";
}
